      if (this.paragraphIndex < this.paragraphs.length - 1) {
        // Avancer au paragraphe suivant
        this.paragraphIndex++;
        // Changer l'image après un délai
        setTimeout(() => {
          this.currentImage = this.images[this.paragraphIndex];
        }, 500); // Ajouter un léger délai pour l'image
      } else {
