import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.flat-map.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import ProductSalesItem from "@/components/ProductSalesItem.vue";
export default {
  name: "SalesDashboard",
  components: {
    NavBar,
    SalesSummary,
    ProductSalesList
  },
  data() {
    return {
      summary: {
        total_sales: 0,
        product_sales: []
      },
      authToken: localStorage.getItem('authToken'),
      apiLink: localStorage.getItem('apiLink') || 'http://localhost:8080/api/v1'
    };
  },
  methods: {
    async fetchSales() {
      try {
        const url = `${this.apiLink}/models/C_Order?$filter=DocStatus eq 'IP' AND IsSOTrx eq true&$select=C_Order_ID,DocumentNo,DateOrdered&$expand=C_OrderLine($select=M_Product_ID,QtyOrdered,LineNetAmt;$expand=M_Product_ID($select=Name))`;
        const headers = this.authToken ? {
          'Authorization': `Bearer ${this.authToken}`
        } : {};
        const response = await fetch(url, {
          headers
        });
        if (!response.ok) throw new Error(`Erreur réseau: ${response.statusText}`);
        const data = await response.json();
        // Flatten all order lines
        const lines = data.records.flatMap(order => order.C_OrderLine.map(line => ({
          productId: line.M_Product_ID.id,
          name: line.M_Product_ID.Name,
          amount: parseFloat(line.LineNetAmt),
          qty: parseFloat(line.QtyOrdered)
        })));

        // Total sales amount
        const totalSales = lines.reduce((sum, l) => sum + l.amount, 0);

        // Group by product
        const map = {};
        lines.forEach(l => {
          if (!map[l.productId]) {
            map[l.productId] = {
              productId: l.productId,
              name: l.name,
              total_sales: 0,
              total_qty: 0
            };
          }
          map[l.productId].total_sales += l.amount;
          map[l.productId].total_qty += l.qty;
        });
        const productSales = Object.values(map);
        this.summary.total_sales = totalSales;
        this.summary.product_sales = productSales;
      } catch (error) {
        console.error("Erreur de chargement des ventes :", error);
      }
    }
  },
  mounted() {
    this.fetchSales();
  }
};
